import { ref } from 'vue';
import { invoke } from '@tauri-apps/api/tauri';
import { listen, UnlistenFn } from '@tauri-apps/api/event';
import { nanoid } from 'nanoid';

export interface AIMessage {
  id: string;
  role: 'user' | 'assistant' | 'system';
  content: string;
  timestamp: Date;
  streaming?: boolean;
}

export interface AISession {
  id: string;
  messages: AIMessage[];
  model: string;
  isThinking: boolean;
}

const sessions = ref<Map<string, AISession>>(new Map());

export function useAI() {
  async function sendPrompt(tabId: string, prompt: string, model?: string) {
    const session = sessions.value.get(tabId) ?? { id: tabId, messages: [], model: model || 'deepseek-coder:6.7b', isThinking: false };
    sessions.value.set(tabId, session);

    session.messages.push({ id: nanoid(), role: 'user', content: prompt, timestamp: new Date() });
    const assistantMessage: AIMessage = { id: nanoid(), role: 'assistant', content: '', timestamp: new Date(), streaming: true };
    session.messages.push(assistantMessage);
    session.isThinking = true;

    const sessionId = nanoid();
    let unlisten: UnlistenFn | null = null;
    let unlistenDone: UnlistenFn | null = null;

    try {
      unlisten = await listen<string>(`ollama://stream/${sessionId}`, (event) => {
        assistantMessage.content += event.payload;
      });

      unlistenDone = await listen<boolean>(`ollama://stream/${sessionId}/done`, () => {
        assistantMessage.streaming = false;
        session.isThinking = false;
        if (unlisten) unlisten();
        if (unlistenDone) unlistenDone();
      });

      await invoke('query_ollama_stream', { prompt, model: session.model, session_id: sessionId });
    } catch (error) {
      assistantMessage.content = `Error: ${error}`;
      assistantMessage.streaming = false;
      session.isThinking = false;
    }
  }

  function getSession(tabId: string) {
    if (!sessions.value.has(tabId)) {
      sessions.value.set(tabId, { id: tabId, messages: [], model: 'deepseek-coder:6.7b', isThinking: false });
    }
    return sessions.value.get(tabId)!;
  }

  function clearSession(tabId: string) {
    const s = sessions.value.get(tabId);
    if (s) s.messages = [];
  }

  function setModel(tabId: string, model: string) {
    const s = sessions.value.get(tabId);
    if (s) s.model = model;
  }

  return { sendPrompt, getSession, clearSession, setModel, sessions };
}


