use tauri::{AppHandle, Manager, Runtime};
use std::process::{Command, Stdio};
use std::io::{BufReader, BufRead};
use std::thread;

#[tauri::command]
pub fn claude_spawn_and_stream(app: AppHandle, prompt: String, session_id: String) -> Result<u32, String> {
    // Replace "claude-code" with the actual CLI name you use
    let mut child = Command::new("claude-code")
        .arg("--format") // example, add your args
        .arg("json")
        .stdin(Stdio::piped())
        .stdout(Stdio::piped())
        .spawn()
        .map_err(|e| format!("spawn_error: {}", e))?;

    // write prompt to stdin (if the CLI expects stdin)
    if let Some(mut stdin) = child.stdin.take() {
        use std::io::Write;
        let p = prompt.clone();
        thread::spawn(move || {
            let _ = stdin.write_all(p.as_bytes());
            let _ = stdin.flush();
        });
    }

    // stream stdout
    if let Some(stdout) = child.stdout.take() {
        let reader = BufReader::new(stdout);
        let h = app.clone();
        let sid = session_id.clone();
        thread::spawn(move || {
            for line in reader.lines() {
                if let Ok(l) = line {
                    // Emit incremental chunk events to the frontend
                    let _ = h.emit_all("claude-stream", serde_json::json!({
                        "session_id": sid,
                        "chunk": l
                    }));
                }
            }
            // finished
            let _ = h.emit_all("claude-stream-done", serde_json::json!({
                "session_id": sid
            }));
        });
    }

    Ok(child.id())
}

