from collections import defaultdict, deque

def topological_sort_kahn(graph: dict, num_nodes: int) -> list:
    """Kahn's algorithm (BFS-based)"""
    in_degree = defaultdict(int)

    for node in graph:
        for neighbor in graph[node]:
            in_degree[neighbor] += 1

    queue = deque([node for node in range(num_nodes) if in_degree[node] == 0])
    result = []

    while queue:
        node = queue.popleft()
        result.append(node)

        for neighbor in graph[node]:
            in_degree[neighbor] -= 1
            if in_degree[neighbor] == 0:
                queue.append(neighbor)

    if len(result) != num_nodes:
        return []  # Cycle detected
    return result

def topological_sort_dfs(graph: dict) -> list:
    """DFS-based (reverse post-order)"""
    visited = set()
    rec_stack = set()  # For cycle detection
    result = []

    def dfs(node):
        if node in rec_stack:
            return False  # Cycle
        if node in visited:
            return True

        visited.add(node)
        rec_stack.add(node)

        for neighbor in graph.get(node, []):
            if not dfs(neighbor):
                return False

        rec_stack.remove(node)
        result.append(node)
        return True

    for node in graph:
        if node not in visited:
            if not dfs(node):
                return []  # Cycle detected

    return result[::-1]

# Example: Course scheduling
def can_finish(num_courses: int, prerequisites: list) -> bool:
    graph = defaultdict(list)
    for course, prereq in prerequisites:
        graph[prereq].append(course)

    return len(topological_sort_kahn(graph, num_courses)) == num_courses
```

Use cases: build systems, package dependencies, task scheduling, course prerequisites.

