import bisect

def lis_fast(nums: list) -> int:
    tails = []  # tails[i] = smallest ending element for LIS of length i+1

    for num in nums:
        pos = bisect.bisect_left(tails, num)
        if pos == len(tails):
            tails.append(num)
        else:
            tails[pos] = num

    return len(tails)
