    last_hash = get_page_hash(url)

    while True:
        time.sleep(interval)
        try:
            current_hash = get_page_hash(url)
            if current_hash != last_hash:
                notify(f"CHANGE DETECTED: {url}")
                last_hash = current_hash
        except Exception as e:
            notify(f"Error checking {url}: {e}")

